#define NOTE_TABLE note_table_pal

// SID utility routines

void sid_init() {
  SID.flt_freq = 0xff;
  SID.flt_ctrl = 0xff;
  SID.amp = 0xff;
}
void sid_stop() {
  SID.flt_freq = 0x00;
  SID.flt_ctrl = 0x00;
  SID.amp = 0x00;
}
#define SID_PULSE_DECAY(voice, period) \
  SID.voice.ctrl = 0x00; \
  SID.voice.freq = period; \
  SID.voice.pw = 0x7f; \
  SID.voice.ad = 0x88; \
  SID.voice.sr = 0x48; \
  SID.voice.ctrl = 0x31; \

// music player logic

byte music_index = 0;
byte cur_duration = 0;

const byte music1[]; // music data -- see end of file
const byte* music_ptr = music1;

byte next_music_byte() {
  return *music_ptr++;
}

void play_music() {
  static byte chs = 0;
  sid_init();
  if (music_ptr) {
    // run out duration timer yet?
    while (cur_duration == 0) {
      // fetch next byte in score
      byte note = next_music_byte();
      // is this a note?
      if ((note & 0x80) == 0) {
        int period = NOTE_TABLE[note & 63];
        // see which pulse generator is free
        if (!(chs & 1)) {
          SID_PULSE_DECAY(v1, period);
          chs |= 1;
        } else if (!(chs & 2)) {
          SID_PULSE_DECAY(v2, period);
          chs |= 2;
        } else if (!(chs & 4)) {
          SID_PULSE_DECAY(v3, period);
          chs |= 4;
        }
      } else {
        // end of score marker
        if (note == 0xff)
          music_ptr = NULL;
        // set duration until next note
        cur_duration = note & 63;
        // reset channel used mask
        chs = 0;
      }
    }
    cur_duration--;
  }
}

void start_music(const byte* music) {
  music_ptr = music;
  cur_duration = 0;
}

void music_stop()
{
  musicPlaying = false;
  sid_stop();
}

void music_update()
{
  if (musicPlaying)
  {
    if (music_ptr)
    {
      play_music();
    }
  }
}

void PlaySong()
{
  sid_init();
  musicPlaying = true;
  music_ptr = 0;
  //while (1)
  {
    if (!music_ptr)
      start_music(music1);
    wait_vblank(0);
    play_music();
  }
}

